mapper.select:
/** selectBoard */ SELECT (ROW_NUMBER() OVER()) AS ROWNUM ,IDX ,CONTENTS ,CREATED_AT ,CREATED_BY ,TITLE FROM TB_BOARD WHERE 1=1
AND TITLE like '%' || #{title} || '%'
AND TO_CHAR(CREATED_AT, 'YYYY-MM-DD') like '%' || #{contents} || '%'
AND TO_CHAR(CREATED_AT, 'YYYY-MM-DD') >= #{createdStart}
AND #{createdEnd} >= TO_CHAR(CREATED_AT, 'YYYY-MM-DD')
ORDER BY IDX DESC LIMIT #{pageSize} OFFSET (#{pageNumber} - 1) * #{pageSize}

mapper.insert:
/** insertBoard */
    INSERT INTO TB_BOARD
    (
    TITLE
    , CONTENTS
    , CREATED_BY
    , CREATED_AT
    ) VALUES (
    #{title}
    , #{contents}
    , #{createdBy}
    , NOW()
    )

mapper.update:
    /** updateBoard */
        UPDATE
        	TB_BOARD
        SET
	        TITLE = #{title}
	        ,CONTENTS = #{contents}
	        ,CREATED_BY = #{contents}
	        ,CREATED_AT = NOW()
        WHERE 1=1
        	AND IDX = #{idx}
